    return (
      <div className="job_container">
        <div>
          <article>
            <h2>{this.state.job[0][0]}</h2>

          </article>

          <section className="card-aspects__container">
            <ul>
              <li>
                <span>
                  <img src={location} alt="location" /> {this.state.job[0][1]}
